var isObject = require('./is-object');

module.exports = {
  getTagName: function(vdom) {
    return vdom[0];
  },
  getAttributes: function(vdom) {
    return isObject(vdom[1]) ? vdom[1] : null;
  },
  getChildren: function(vdom) {
    if (vdom[2]) {
      return vdom.slice(2);
    }
    return !isObject(vdom[1]) && vdom[1] != null ? vdom.slice(1) : null;
  }
};
